    public void onLoginClick(View dialog) {
        // User touched the dialog's positive button
        Log.w("login", "login clicked");
        // "USER"=>user model function result of login,
        // "MENUS"=>list of menus for given user groupby date
        // "ORG" => org for user
        APIThreadPool apiInstance = APIThreadPool.getInstance();
        HashMap<String, String> postData = this.checkForSavedCredentials();
        // get username and password
        if(postData == null) { // could not find login credentials on the phone
            postData = new HashMap<String, String>();
            EditText usernameField = (EditText) findViewById(R.id.loginUsername);
            EditText passwordField = (EditText) findViewById(R.id.loginPassword);
            String email = usernameField.getText().toString();
            String password = passwordField.getText().toString();
            postData.put("email", email); // this is actually an email
            postData.put("password", password);
        }
        apiInstance.callAPIAsync(postData, "/user/login", "loginCallback", this);
    }

